#!/usr/bin/env ruby
require 'clipboard'

digits_we_need = 5
number_string = ""
max_location = number_string.length - digits_we_need 
max_product = 0

for location in (0..max_location)
	newstring = number_string[location..(location+digits_we_need-1)]
	product = 1
	for digit in newstring.each_char
		product *= digit.to_i
	end
	max_product = product if product > max_product
end

Clipboard.copy(max_product)
puts "#{max_product} is copied to the clipboard."
